#!/system/bin/sh

# LineageOS for microG 15.1 Release keys
media_cert_release=""
platform_cert_release=""
shared_cert_release=""
release_cert=""

media_key_release=""
platform_key_release=""
shared_key_release=""
release_key=""


# Own Keys
my_platform_key_release=""
my_platform_cert_release=""
my_media_key_release=""
my_media_cert_release=""
my_shared_key_release=""
my_shared_cert_release=""
my_release_key=""
my_release_cert=""


PACKAGES=/data/system/packages.xml
PACKAGES_BACKUP=/data/system/packages-backup.xml

# If there's a "backup" copy, then the current packages.xml might
# be corrupted, so overwrite it with the backup copy. This is what
# PackageManager would do.
if [ -f $PACKAGES_BACKUP ]; then
    mv $PACKAGES_BACKUP $PACKAGES
    echo "Overwriting $PACKAGES with $PACKAGES_BACKUP"
fi

if [ ! -f $PACKAGES ]; then
    echo "Error: $PACKAGES not found"
    exit 1
fi

# Save a copy of the current file, not to be confused with
# packages-backup.xml, which is used and generated by Android
echo "Saving backup as $PACKAGES.bak"
cp $PACKAGES $PACKAGES.bak

sed -i "s#$media_cert_release#$my_media_cert_release#g" $PACKAGES
sed -i "s#$platform_cert_release#$my_platform_cert_release#g" $PACKAGES
sed -i "s#$shared_cert_release#$my_shared_cert_release#g" $PACKAGES
sed -i "s#$release_cert#$my_release_cert#g" $PACKAGES
sed -i "s#$media_key_release#$my_media_key_release#g" $PACKAGES
sed -i "s#$platform_key_release#$my_platform_key_release#g" $PACKAGES
sed -i "s#$shared_key_release#$my_shared_key_release#g" $PACKAGES
sed -i "s#$release_key#$my_release_key#g" $PACKAGES

chmod 660 $PACKAGES
chown system:system $PACKAGES

echo "Done"
